import React from 'react';
import { useTax } from '../context/TaxContext';
import { ArrowRight, ArrowLeft } from 'lucide-react';

export default function Deductions() {
  const { setStep } = useTax();

  return (
    <div className="deductions-page" style={{ maxWidth: '800px', margin: '0 auto', padding: '2rem' }}>
      <div className="page-header">
        <h1>üè¶ Deductions & Credits</h1>
        <p style={{ color: '#718096' }}>Coming soon - We're building this section now</p>
      </div>

      <div style={{ 
        background: '#F7FAFC', 
        borderRadius: '12px', 
        padding: '3rem', 
        textAlign: 'center',
        marginBottom: '2rem'
      }}>
        <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üöß</div>
        <h2 style={{ color: '#1A365D', marginBottom: '0.5rem' }}>Under Construction</h2>
        <p style={{ color: '#718096' }}>
          This section will allow you to claim deductions and credits for both US and Canada.
        </p>
        <ul style={{ 
          textAlign: 'left', 
          maxWidth: '400px', 
          margin: '1.5rem auto 0',
          color: '#4A5568'
        }}>
          <li>US: Standard/Itemized deductions</li>
          <li>Canada: RRSP, TFSA, childcare</li>
          <li>Foreign Tax Credits</li>
          <li>Home office expenses</li>
        </ul>
      </div>

      <div className="form-actions">
        <button className="btn-secondary" onClick={() => setStep('income')}>
          <ArrowLeft size={16} /> Back
        </button>
        <button className="btn-primary" onClick={() => setStep('review')}>
          Next: Review <ArrowRight size={16} />
        </button>
      </div>

      <style>{`
        .btn-primary, .btn-secondary {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          padding: 0.75rem 1.5rem;
          border-radius: 8px;
          font-weight: 600;
          cursor: pointer;
        }
        .btn-primary { background: #1A365D; color: white; border: none; }
        .btn-secondary { background: white; color: #4A5568; border: 1px solid #E2E8F0; }
        .form-actions { display: flex; justify-content: space-between; margin-top: 2rem; }
      `}</style>
    </div>
  );
}
